package com.caveOfProgramming;

public class IfStatments {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
boolean cond =3<5;
		
	System.out.println(cond);	
	
	int loop =0;
	while(true) {
		System.out.println("Looping: " +loop);
		
		if(loop==5) {
			break;
		}
		loop++;
		System.out.println("Running");
		}
	}
	}


