package com.syntax.class03;

public class PossitiveOrNegative {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int x = -7;
		if (x > 0) {
			System.out.println(x + " " + "is positive");
		} else if (x < 0) {
			System.out.println(x + " " + " is negative");
		}

	}

}
