package replit;

public class Task061 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		/*Write a program that uses for loop to prints multiplication of number 3 from 1 to 10
		 * Expected Output:
		 * 3*1=3
		 */
		
		for (int i=1; i<=10;i++) {
			System.out.println(3+"*" + i + "=" +i * 3);
		}
	}

}
